mapper.select:
select ,receiver, receiver_address, receiver_mobile, logistics, logistics_sn,
logistics_status  from orders o, orders_logistics l where is_delete = 0

  and o.order_sn = #{search.orderSn}


  and o.partner_id = #{search.partnerId}


  and o.partner_sn = #{search.partnerSn}


  and l.receiver_mobile = #{search.receiverMobile}


  and l.buy_time  >= #{search.buyStartTime}


  and l.buy_time <= #{search.buyEndTime}


  and l.delivery_status = #{search.deliveryStatus}

order by o.id desc limit #{page.start}, #{page.pageSize}

select count(1)  from orders o, orders_logistics l where is_delete = 0

  and o.order_sn = #{search.orderSn}


  and o.partner_id = #{search.partnerId}


  and o.partner_sn = #{search.partnerSn}


  and l.receiver_mobile = #{search.receiverMobile}


  and l.buy_time  >= #{search.buyStartTime}


  and l.buy_time <= #{search.buyEndTime}


  and l.delivery_status = #{search.deliveryStatus}
